import React, { useState, useEffect } from 'react';
import { useTranslation } from 'react-i18next';
import { 
  AcademicCapIcon, 
  ClockIcon, 
  CurrencyRupeeIcon,
  StarIcon,
  BookmarkIcon,
  FunnelIcon
} from '@heroicons/react/24/outline';

const Courses = () => {
  const { t } = useTranslation();
  const [courses, setCourses] = useState([]);
  const [filteredCourses, setFilteredCourses] = useState([]);
  const [filters, setFilters] = useState({
    category: 'all',
    duration: 'all',
    level: 'all'
  });

  const coursesData = [
    {
      id: 1,
      name: "Computer Science Engineering",
      category: "Engineering",
      duration: "4 years",
      level: "Undergraduate",
      rating: 4.8,
      students: 15420,
      fee: "₹2,50,000/year",
      match: 95,
      objectives: "Learn programming, algorithms, software development, and computer systems",
      prerequisites: "Mathematics, Physics (12th grade)",
      careerScope: "Software Developer, Data Scientist, AI Engineer, System Architect",
      financialScope: "₹8-25 LPA starting salary",
      futureScope: "High demand in tech industry, AI/ML specializations",
      image: "/api/placeholder/300/200"
    },
    {
      id: 2,
      name: "MBBS (Bachelor of Medicine)",
      category: "Medical",
      duration: "5.5 years",
      level: "Undergraduate",
      rating: 4.9,
      students: 8750,
      fee: "₹5,00,000/year",
      match: 88,
      objectives: "Comprehensive medical education, patient care, clinical skills",
      prerequisites: "Biology, Chemistry, Physics (12th grade), NEET qualification",
      careerScope: "Doctor, Surgeon, Medical Researcher, Healthcare Administrator",
      financialScope: "₹6-15 LPA starting, ₹50+ LPA experienced",
      futureScope: "Always in demand, specialization opportunities",
      image: "/api/placeholder/300/200"
    },
    {
      id: 3,
      name: "Business Administration (MBA)",
      category: "Business",
      duration: "2 years",
      level: "Postgraduate",
      rating: 4.6,
      students: 12300,
      fee: "₹8,00,000/year",
      match: 82,
      objectives: "Leadership skills, strategic thinking, business management",
      prerequisites: "Bachelor's degree, CAT/GMAT scores",
      careerScope: "Manager, Consultant, Entrepreneur, Business Analyst",
      financialScope: "₹12-30 LPA starting salary",
      futureScope: "Leadership roles, startup opportunities",
      image: "/api/placeholder/300/200"
    },
    {
      id: 4,
      name: "Data Science",
      category: "Technology",
      duration: "3 years",
      level: "Undergraduate",
      rating: 4.7,
      students: 9850,
      fee: "₹3,00,000/year",
      match: 91,
      objectives: "Statistical analysis, machine learning, data visualization",
      prerequisites: "Mathematics, Statistics (12th grade)",
      careerScope: "Data Scientist, ML Engineer, Business Intelligence Analyst",
      financialScope: "₹10-20 LPA starting salary",
      futureScope: "Rapidly growing field, AI integration",
      image: "/api/placeholder/300/200"
    },
    {
      id: 5,
      name: "Graphic Design",
      category: "Arts",
      duration: "3 years",
      level: "Undergraduate",
      rating: 4.4,
      students: 6420,
      fee: "₹1,50,000/year",
      match: 76,
      objectives: "Visual communication, creative design, digital media",
      prerequisites: "Portfolio, creative aptitude",
      careerScope: "Graphic Designer, UI/UX Designer, Creative Director",
      financialScope: "₹4-12 LPA starting salary",
      futureScope: "Digital transformation, freelance opportunities",
      image: "/api/placeholder/300/200"
    },
    {
      id: 6,
      name: "Civil Engineering",
      category: "Engineering",
      duration: "4 years",
      level: "Undergraduate",
      rating: 4.5,
      students: 11200,
      fee: "₹2,00,000/year",
      match: 79,
      objectives: "Infrastructure design, construction management, urban planning",
      prerequisites: "Mathematics, Physics (12th grade)",
      careerScope: "Civil Engineer, Project Manager, Urban Planner",
      financialScope: "₹5-15 LPA starting salary",
      futureScope: "Smart cities, sustainable construction",
      image: "/api/placeholder/300/200"
    }
  ];

  useEffect(() => {
    setCourses(coursesData);
    setFilteredCourses(coursesData);
  }, []);

  useEffect(() => {
    let filtered = courses;

    if (filters.category !== 'all') {
      filtered = filtered.filter(course => course.category === filters.category);
    }
    if (filters.duration !== 'all') {
      filtered = filtered.filter(course => course.duration.includes(filters.duration));
    }
    if (filters.level !== 'all') {
      filtered = filtered.filter(course => course.level === filters.level);
    }

    setFilteredCourses(filtered);
  }, [filters, courses]);

  const handleFilterChange = (filterType, value) => {
    setFilters({
      ...filters,
      [filterType]: value
    });
  };

  return (
    <div className="min-h-screen bg-gray-50 py-8">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="mb-8">
          <h1 className="section-title">{t('courses.title')}</h1>
          <p className="text-gray-600">Discover courses tailored to your interests and career goals</p>
        </div>

        {/* Filters */}
        <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-6 mb-8">
          <div className="flex items-center mb-4">
            <FunnelIcon className="h-5 w-5 text-gray-400 mr-2" />
            <h3 className="text-lg font-semibold text-gray-900">Filter Courses</h3>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">Category</label>
              <select
                value={filters.category}
                onChange={(e) => handleFilterChange('category', e.target.value)}
                className="input-field"
              >
                <option value="all">All Categories</option>
                <option value="Engineering">Engineering</option>
                <option value="Medical">Medical</option>
                <option value="Business">Business</option>
                <option value="Technology">Technology</option>
                <option value="Arts">Arts</option>
              </select>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">Duration</label>
              <select
                value={filters.duration}
                onChange={(e) => handleFilterChange('duration', e.target.value)}
                className="input-field"
              >
                <option value="all">All Durations</option>
                <option value="2">2 Years</option>
                <option value="3">3 Years</option>
                <option value="4">4 Years</option>
                <option value="5">5+ Years</option>
              </select>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">Level</label>
              <select
                value={filters.level}
                onChange={(e) => handleFilterChange('level', e.target.value)}
                className="input-field"
              >
                <option value="all">All Levels</option>
                <option value="Undergraduate">Undergraduate</option>
                <option value="Postgraduate">Postgraduate</option>
              </select>
            </div>
          </div>
        </div>

        {/* Course Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {filteredCourses.map((course) => {
            // Check if this course is in user's recommendations
            const quizResults = JSON.parse(localStorage.getItem('quizResults') || '{}');
            const userRecommendations = quizResults.recommendations || [];
            const isRecommended = userRecommendations.some(rec => rec.name === course.name);
            const recommendedCourse = userRecommendations.find(rec => rec.name === course.name);
            const displayMatch = isRecommended ? recommendedCourse.match : course.match;

            return (
            <div key={course.id} className="bg-white rounded-xl shadow-sm border border-gray-200 overflow-hidden hover:shadow-lg transition-shadow duration-200">
              <div className="h-48 bg-gradient-to-br from-primary-500 to-secondary-500 flex items-center justify-center">
                <AcademicCapIcon className="h-16 w-16 text-white" />
              </div>

              <div className="p-6">
                <div className="flex items-center justify-between mb-2">
                  <div className="flex items-center space-x-2">
                    <span className={`text-xs px-2 py-1 rounded ${
                      displayMatch >= 90 ? 'bg-green-100 text-green-800' :
                      displayMatch >= 80 ? 'bg-blue-100 text-blue-800' :
                      'bg-gray-100 text-gray-800'
                    }`}>
                      {displayMatch}% Match
                    </span>
                    {isRecommended && (
                      <span className="bg-yellow-100 text-yellow-800 text-xs px-2 py-1 rounded">
                        Recommended
                      </span>
                    )}
                  </div>
                  <BookmarkIcon className="h-5 w-5 text-gray-400 hover:text-primary-600 cursor-pointer" />
                </div>

                <h3 className="text-lg font-semibold text-gray-900 mb-2">{course.name}</h3>
                <p className="text-sm text-gray-600 mb-4">{course.category} • {course.level}</p>

                <div className="flex items-center mb-4">
                  <div className="flex items-center mr-4">
                    <StarIcon className="h-4 w-4 text-yellow-400 fill-current" />
                    <span className="text-sm text-gray-600 ml-1">{course.rating}</span>
                  </div>
                  <div className="flex items-center mr-4">
                    <ClockIcon className="h-4 w-4 text-gray-400" />
                    <span className="text-sm text-gray-600 ml-1">{course.duration}</span>
                  </div>
                  <div className="flex items-center">
                    <CurrencyRupeeIcon className="h-4 w-4 text-gray-400" />
                    <span className="text-sm text-gray-600 ml-1">{course.fee}</span>
                  </div>
                </div>

                <div className="space-y-2 text-sm text-gray-600 mb-4">
                  <p><strong>Career Scope:</strong> {course.careerScope}</p>
                  <p><strong>Financial Scope:</strong> {course.financialScope}</p>
                </div>

                <button className="w-full btn-primary">
                  View Details
                </button>
              </div>
            </div>
            );
          })}
        </div>

        {filteredCourses.length === 0 && (
          <div className="text-center py-12">
            <AcademicCapIcon className="h-16 w-16 text-gray-400 mx-auto mb-4" />
            <h3 className="text-lg font-medium text-gray-900 mb-2">No courses found</h3>
            <p className="text-gray-600">Try adjusting your filters to see more results.</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Courses;
