import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth0 } from '@auth0/auth0-react';

// Sample course trail data - 2 weeks of content
const courseTrailData = [
  {
    day: 1,
    title: 'Introduction to Career Paths',
    description: 'Understand different career options and how to choose the right one',
    duration: '30 min',
    type: 'video',
    content: 'https://example.com/videos/career-intro',
    quiz: {
      questions: [
        {
          id: 1,
          question: 'What are the key factors to consider when choosing a career?',
          options: [
            'Salary only',
            'Interest, skills, and market demand',
            'Your friend\'s choice',
            'Location only'
          ],
          correctAnswer: 1
        },
        // Add more questions
      ]
    },
    completed: false,
    quizScore: null
  },
  // Add 13 more days of content
];

const CourseTrail = () => {
  const [currentDay, setCurrentDay] = useState(1);
  const [trailProgress, setTrailProgress] = useState({});
  const [quizAnswers, setQuizAnswers] = useState({});
  const [showQuiz, setShowQuiz] = useState(false);
  const { isAuthenticated, loginWithRedirect } = useAuth0();
  const navigate = useNavigate();

  useEffect(() => {
    // Load progress from localStorage
    const savedProgress = JSON.parse(localStorage.getItem('courseTrailProgress')) || {};
    setTrailProgress(savedProgress);
  }, []);

  const handleCompleteDay = () => {
    const updatedProgress = {
      ...trailProgress,
      [currentDay]: { completed: true, completedAt: new Date().toISOString() }
    };
    setTrailProgress(updatedProgress);
    localStorage.setItem('courseTrailProgress', JSON.stringify(updatedProgress));
    setShowQuiz(true);
  };

  const handleQuizSubmit = (day) => {
    // Calculate score and save
    const dayData = courseTrailData.find(d => d.day === day);
    const score = calculateScore(dayData.quiz.questions, quizAnswers[day] || {});

    const updatedProgress = {
      ...trailProgress,
      [day]: { 
        ...trailProgress[day],
        quizScore: score,
        completed: true,
        completedAt: new Date().toISOString()
      }
    };

    setTrailProgress(updatedProgress);
    localStorage.setItem('courseTrailProgress', JSON.stringify(updatedProgress));
    setShowQuiz(false);

    // Move to next day if not the last day
    if (currentDay < 14) {
      setCurrentDay(currentDay + 1);
    }
  };

  const calculateScore = (questions, answers) => {
    let correct = 0;
    questions.forEach(q => {
      if (answers[q.id] === q.correctAnswer) correct++;
    });
    return Math.round((correct / questions.length) * 100);
  };

  const handleAnswerSelect = (questionId, answerIndex) => {
    setQuizAnswers(prev => ({
      ...prev,
      [currentDay]: {
        ...prev[currentDay],
        [questionId]: answerIndex
      }
    }));
  };

  if (!isAuthenticated) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-50">
        <div className="text-center p-8 bg-white rounded-lg shadow-md">
          <h2 className="text-2xl font-bold mb-4">Sign In to Access Course Trail</h2>
          <p className="mb-6">Please sign in to access your personalized 2-week career training program.</p>
          <button
            onClick={() => loginWithRedirect()}
            className="px-6 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 transition-colors"
          >
            Sign In
          </button>
        </div>
      </div>
    );
  }

  const currentDayData = courseTrailData.find(day => day.day === currentDay);
  const isDayCompleted = trailProgress[currentDay]?.completed;

  return (
    <div className="min-h-screen bg-gray-50 py-8 px-4">
      <div className="max-w-4xl mx-auto">
        <h1 className="text-3xl font-bold text-center mb-8">2-Week Career Training Program</h1>

        {/* Progress Tracker */}
        <div className="mb-8">
          <h2 className="text-xl font-semibold mb-4">Your Progress</h2>
          <div className="flex flex-wrap gap-2">
            {Array.from({ length: 14 }).map((_, i) => {
              const dayNum = i + 1;
              const isCompleted = trailProgress[dayNum]?.completed;
              const isCurrent = dayNum === currentDay;

              return (
                <button
                  key={dayNum}
                  onClick={() => setCurrentDay(dayNum)}
                  className={`w-10 h-10 rounded-full flex items-center justify-center font-medium
                    ${isCurrent ? 'bg-blue-600 text-white' : ''}
                    ${isCompleted ? 'bg-green-100 text-green-700' : 'bg-gray-200'}
                    ${!isCurrent && !isCompleted ? 'hover:bg-gray-300' : ''}`}
                >
                  {dayNum}
                </button>
              );
            })}
          </div>
        </div>

        {/* Current Day Content */}
        <div className="bg-white rounded-lg shadow-md p-6 mb-6">
          <div className="flex justify-between items-center mb-4">
            <h2 className="text-2xl font-bold">Day {currentDay}: {currentDayData.title}</h2>
            <span className="px-3 py-1 bg-blue-100 text-blue-800 rounded-full text-sm">
              {currentDayData.duration}
            </span>
          </div>

          <p className="text-gray-700 mb-6">{currentDayData.description}</p>

          {currentDayData.type === 'video' && (
            <div className="mb-6 aspect-video bg-gray-200 rounded-lg flex items-center justify-center">
              <p className="text-gray-500">Video Content: {currentDayData.content}</p>
              {/* In a real app, you would embed a video player here */}
            </div>
          )}

          {!isDayCompleted && !showQuiz && (
            <button
              onClick={handleCompleteDay}
              className="px-4 py-2 bg-green-600 text-white rounded-md hover:bg-green-700 transition-colors"
            >
              Mark as Completed
            </button>
          )}

          {isDayCompleted && !showQuiz && (
            <div className="mt-4 p-4 bg-green-50 text-green-800 rounded-md">
              <p className="font-medium">âœ“ Completed on {new Date(trailProgress[currentDay].completedAt).toLocaleDateString()}</p>
              {trailProgress[currentDay].quizScore !== null && (
                <p className="mt-1">Quiz Score: {trailProgress[currentDay].quizScore}%</p>
              )}
              <button
                onClick={() => setShowQuiz(true)}
                className="mt-2 text-blue-600 hover:underline text-sm"
              >
                Retake Quiz
              </button>
            </div>
          )}
        </div>

        {/* Quiz Section */}
        {showQuiz && (
          <div className="bg-white rounded-lg shadow-md p-6">
            <h3 className="text-xl font-semibold mb-4">Quiz: {currentDayData.title}</h3>

            {currentDayData.quiz.questions.map((question, qIndex) => (
              <div key={question.id} className="mb-6">
                <p className="font-medium mb-2">{qIndex + 1}. {question.question}</p>
                <div className="space-y-2">
                  {question.options.map((option, oIndex) => (
                    <label key={oIndex} className="flex items-center space-x-2 cursor-pointer">
                      <input
                        type="radio"
                        name={`question-${question.id}`}
                        checked={(quizAnswers[currentDay]?.[question.id] === oIndex)}
                        onChange={() => handleAnswerSelect(question.id, oIndex)}
                        className="h-4 w-4 text-blue-600"
                      />
                      <span>{option}</span>
                    </label>
                  ))}
                </div>
              </div>
            ))}

            <div className="flex justify-between mt-6">
              <button
                onClick={() => setShowQuiz(false)}
                className="px-4 py-2 border border-gray-300 rounded-md hover:bg-gray-50"
              >
                Back to Content
              </button>
              <button
                onClick={() => handleQuizSubmit(currentDay)}
                className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700"
              >
                Submit Quiz
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default CourseTrail;
