import React, { useState, useEffect } from 'react';
import { useTranslation } from 'react-i18next';
import {
  MagnifyingGlassIcon,
  FunnelIcon,
  MapPinIcon,
  AcademicCapIcon,
  CurrencyRupeeIcon,
  StarIcon,
  BuildingOfficeIcon,
  GlobeAltIcon,
  ChartBarIcon,
  ArrowTopRightOnSquareIcon,
  MapIcon,
  XMarkIcon
} from '@heroicons/react/24/outline';
import { collegesData, collegeCategories, collegeTypes, locations } from '../data/collegesData';

const Colleges = () => {
  const { t } = useTranslation();
  const [colleges, setColleges] = useState([]);
  const [filteredColleges, setFilteredColleges] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('All');
  const [selectedType, setSelectedType] = useState('All');
  const [selectedLocation, setSelectedLocation] = useState('All');
  const [selectedCollege, setSelectedCollege] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [userLocation, setUserLocation] = useState(null);
  const [routeInfo, setRouteInfo] = useState(null);
  const [loadingRoute, setLoadingRoute] = useState(false);

  // Initialize component with data and user location
  useEffect(() => {
    try {
      setColleges(collegesData);
      setFilteredColleges(collegesData);
      getUserLocation();
    } catch (error) {
      console.error('Error initializing component:', error);
    }
  }, []);

  // Get user's current location
  const getUserLocation = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          setUserLocation({
            lat: position.coords.latitude,
            lng: position.coords.longitude
          });
        },
        (error) => {
          console.log('Location access denied:', error);
          // Fallback to Delhi coordinates
          setUserLocation({ lat: 28.6139, lng: 77.2090 });
        }
      );
    } else {
      // Fallback to Delhi coordinates
      setUserLocation({ lat: 28.6139, lng: 77.2090 });
    }
  };

  // Calculate distance between two coordinates in kilometers using Haversine formula
  const calculateDistance = (lat1, lon1, lat2, lon2) => {
    const R = 6371; // Earth's radius in km
    const dLat = (lat2 - lat1) * Math.PI / 180;
    const dLon = (lon2 - lon1) * Math.PI / 180;
    const a = 
      Math.sin(dLat/2) * Math.sin(dLat/2) +
      Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) * 
      Math.sin(dLon/2) * Math.sin(dLon/2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1-a));
    const distance = R * c; // Distance in km
    return Math.round(distance * 10) / 10; // Round to 1 decimal
  };

  // Handle opening college details modal
  const openModal = (college) => {
    setSelectedCollege(college);
    setShowModal(true);
  };

  // Handle getting directions
  const handleGetDirections = (college) => {
    if (!userLocation || !college.coordinates) {
      alert('Location services are not available.');
      return;
    }

    const { lat, lng } = college.coordinates;
    const url = `https://www.google.com/maps/dir/?api=1&origin=${userLocation.lat},${userLocation.lng}&destination=${lat},${lng}&travelmode=driving`;
    window.open(url, '_blank');
  };

  // Handle closing modal
  const closeModal = () => {
    setShowModal(false);
    setSelectedCollege(null);
  };

  // Main component render
  return (
    <div className="min-h-screen bg-gray-50 py-8">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Header */}
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900 mb-2">Find Colleges</h1>
          <p className="text-gray-600">Discover top colleges and universities across India</p>
        </div>

        {/* Search and Filters */}
        <div className="bg-white rounded-lg shadow-sm p-6 mb-8">
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
            {/* Search */}
            <div className="relative">
              <MagnifyingGlassIcon className="absolute left-3 top-1/2 transform -translate-y-1/2 h-5 w-5 text-gray-400" />
              <input
                type="text"
                placeholder="Search colleges..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              />
            </div>

            {/* Category Filter */}
            <select
              value={selectedCategory}
              onChange={(e) => setSelectedCategory(e.target.value)}
              className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
            >
              <option value="All">All Categories</option>
              {collegeCategories.map(category => (
                <option key={category} value={category}>{category}</option>
              ))}
            </select>

            {/* Type Filter */}
            <select
              value={selectedType}
              onChange={(e) => setSelectedType(e.target.value)}
              className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
            >
              <option value="All">All Types</option>
              {collegeTypes.map(type => (
                <option key={type} value={type}>{type}</option>
              ))}
            </select>

            {/* Location Filter */}
            <select
              value={selectedLocation}
              onChange={(e) => setSelectedLocation(e.target.value)}
              className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
            >
              <option value="All">All Locations</option>
              {locations.map(location => (
                <option key={location} value={location}>{location}</option>
              ))}
            </select>
          </div>
        </div>

        {/* Results Count */}
        <div className="mb-6">
          <p className="text-gray-600">
            Showing {filteredColleges.length} of {colleges.length} colleges
          </p>
        </div>

        {/* Colleges Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {filteredColleges.map(college => (
            <div key={college.id} className="bg-white rounded-lg shadow-sm hover:shadow-md transition-shadow duration-200">
              <div className="p-6">
                <div className="flex items-start justify-between mb-4">
                  <div className="flex items-center space-x-2">
                    {college.category === 'Engineering' ? (
                      <BuildingOfficeIcon className="h-5 w-5" />
                    ) : (
                      <AcademicCapIcon className="h-5 w-5" />
                    )}
                    <span className={`px-2 py-1 rounded-full text-xs font-medium ${
                      college.type === 'Government' ? 'bg-green-100 text-green-800' : 'bg-blue-100 text-blue-800'
                    }`}>
                      {college.type}
                    </span>
                  </div>
                  <span className="px-2 py-1 rounded-full text-xs font-medium bg-gray-100 text-gray-800">
                    NIRF #{college.ranking?.nirf || 'N/A'}
                  </span>
                </div>

                <h3 className="text-lg font-semibold text-gray-900 mb-2">{college.shortName || college.name}</h3>
                <p className="text-sm text-gray-600 mb-3">{college.name}</p>

                <div className="space-y-2 mb-4">
                  <div className="flex items-center text-sm text-gray-600">
                    <MapPinIcon className="h-4 w-4 mr-2" />
                    {college.location}
                  </div>
                  {college.fees?.total && (
                    <div className="flex items-center text-sm text-gray-600">
                      <CurrencyRupeeIcon className="h-4 w-4 mr-2" />
                      {college.fees.total}
                    </div>
                  )}
                  {college.placements?.averagePackage && (
                    <div className="flex items-center text-sm text-gray-600">
                      <ChartBarIcon className="h-4 w-4 mr-2" />
                      Avg Package: {college.placements.averagePackage}
                    </div>
                  )}
                </div>

                <div className="space-y-2">
                  <button
                    onClick={() => openModal(college)}
                    className="w-full bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 transition-colors text-sm font-medium"
                  >
                    View Details
                  </button>
                  {college.website && (
                    <a
                      href={college.website}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="block w-full text-center mt-2 text-blue-600 hover:underline text-sm"
                    >
                      Visit Website
                    </a>
                  )}
                </div>
              </div>
            </div>
          ))}
        </div>

        {/* College Details Modal */}
        {showModal && selectedCollege && (
          <div 
            className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50 transition-opacity duration-300"
            onClick={closeModal}
          >
            <div 
              className="bg-white rounded-xl shadow-2xl max-w-3xl w-full max-h-[90vh] overflow-y-auto transform transition-all duration-300 scale-95 hover:scale-100"
              onClick={(e) => e.stopPropagation()}
            >
              {/* Header with gradient background */}
              <div className="bg-gradient-to-r from-blue-600 to-blue-800 p-6 rounded-t-xl">
                <div className="flex justify-between items-start">
                  <div>
                    <div className="flex items-center space-x-2 mb-1">
                      <AcademicCapIcon className="h-6 w-6 text-yellow-300" />
                      <span className="text-yellow-300 text-sm font-medium">
                        {selectedCollege.type} • {selectedCollege.category}
                      </span>
                    </div>
                    <h2 className="text-2xl font-bold text-white">{selectedCollege.name}</h2>
                    <div className="flex items-center mt-2 text-blue-100">
                      <MapPinIcon className="h-5 w-5 mr-1" />
                      <span>{selectedCollege.location}</span>
                      {selectedCollege.established && (
                        <span className="ml-3">• Est. {selectedCollege.established}</span>
                      )}
                    </div>
                  </div>
                  <button
                    onClick={closeModal}
                    className="text-white hover:bg-white/20 p-1 rounded-full transition-colors"
                  >
                    <XMarkIcon className="h-6 w-6" />
                  </button>
                </div>

                {/* Quick Stats */}
                <div className="mt-6 grid grid-cols-2 md:grid-cols-4 gap-4">
                  {selectedCollege.ranking?.nirf && (
                    <div className="bg-white/10 backdrop-blur-sm p-3 rounded-lg text-center">
                      <div className="text-2xl font-bold text-yellow-300">#{selectedCollege.ranking.nirf}</div>
                      <div className="text-xs uppercase tracking-wider text-blue-100">NIRF Rank</div>
                    </div>
                  )}
                  {selectedCollege.placements?.averagePackage && (
                    <div className="bg-white/10 backdrop-blur-sm p-3 rounded-lg text-center">
                      <div className="text-xl font-bold text-white">{selectedCollege.placements.averagePackage}</div>
                      <div className="text-xs uppercase tracking-wider text-blue-100">Avg Package</div>
                    </div>
                  )}
                  {selectedCollege.fees?.total && (
                    <div className="bg-white/10 backdrop-blur-sm p-3 rounded-lg text-center">
                      <div className="text-xl font-bold text-white">{selectedCollege.fees.total}</div>
                      <div className="text-xs uppercase tracking-wider text-blue-100">Total Fees</div>
                    </div>
                  )}
                  <div className="bg-white/10 backdrop-blur-sm p-3 rounded-lg text-center">
                    <div className="flex items-center justify-center">
                      {[...Array(5)].map((_, i) => (
                        <StarIcon 
                          key={i} 
                          className={`h-5 w-5 ${i < (selectedCollege.rating || 4) ? 'text-yellow-400' : 'text-gray-400'}`} 
                          fill={i < (selectedCollege.rating || 4) ? 'currentColor' : 'none'}
                        />
                      ))}
                    </div>
                    <div className="text-xs uppercase tracking-wider text-blue-100 mt-1">Rating</div>
                  </div>
                </div>
              </div>

              <div className="p-6 space-y-8">
                {/* Quick Actions */}
                <div className="flex flex-wrap gap-3">
                  <a
                    href={selectedCollege.website}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="flex items-center px-4 py-2 bg-blue-50 text-blue-700 rounded-lg hover:bg-blue-100 transition-colors"
                  >
                    <GlobeAltIcon className="h-5 w-5 mr-2" />
                    Visit Website
                  </a>
                  <button
                    onClick={() => handleGetDirections(selectedCollege)}
                    className="flex items-center px-4 py-2 bg-green-50 text-green-700 rounded-lg hover:bg-green-100 transition-colors"
                  >
                    <MapIcon className="h-5 w-5 mr-2" />
                    Get Directions
                  </button>
                  {selectedCollege.brochure && (
                    <a
                      href={selectedCollege.brochure}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="flex items-center px-4 py-2 bg-purple-50 text-purple-700 rounded-lg hover:bg-purple-100 transition-colors"
                    >
                      <svg className="h-5 w-5 mr-2" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
                      </svg>
                      Download Brochure
                    </a>
                  )}
                </div>

                {/* Fees */}
                {selectedCollege.fees && (
                  <div className="bg-gradient-to-br from-blue-50 to-blue-100 p-6 rounded-2xl border border-blue-100">
                    <div className="flex items-center mb-4">
                      <div className="p-2 bg-blue-100 rounded-lg mr-3">
                        <svg className="h-6 w-6 text-blue-600" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8c-1.657 0-3 .895-3 2s1.343 2 3 2 3 .895 3 2-1.343 2-3 2m0-8c1.11 0 2.08.402 2.599 1M12 8V7m0 1v8m0 0v1m0-1c-1.11 0-2.08-.402-2.599-1M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
                        </svg>
                      </div>
                      <h3 className="text-xl font-bold text-gray-800">Fee Structure</h3>
                    </div>
                    <div className="space-y-3">
                      {selectedCollege.fees.tuition && (
                        <div className="flex justify-between items-center bg-white/80 backdrop-blur-sm p-3 rounded-lg">
                          <div>
                            <p className="text-sm text-gray-500">Tuition Fee</p>
                            <p className="text-gray-600">Per Year</p>
                          </div>
                          <span className="text-lg font-semibold text-gray-800">{selectedCollege.fees.tuition}</span>
                        </div>
                      )}
                      {selectedCollege.fees.hostel && (
                        <div className="flex justify-between items-center bg-white/80 backdrop-blur-sm p-3 rounded-lg">
                          <div>
                            <p className="text-sm text-gray-500">Hostel Fee</p>
                            <p className="text-gray-600">Per Year</p>
                          </div>
                          <span className="text-lg font-semibold text-gray-800">{selectedCollege.fees.hostel}</span>
                        </div>
                      )}
                      {selectedCollege.fees.total && (
                        <div className="flex justify-between items-center bg-blue-600 text-white p-4 rounded-lg mt-4">
                          <p className="font-medium">Total Estimated Cost</p>
                          <span className="text-xl font-bold">{selectedCollege.fees.total}</span>
                        </div>
                      )}
                    </div>
                  </div>
                )}

                {/* Placements */}
                {selectedCollege.placements && (
                  <div className="bg-gradient-to-br from-green-50 to-emerald-50 p-6 rounded-2xl border border-green-100">
                    <div className="flex items-center mb-6">
                      <div className="p-2 bg-green-100 rounded-lg mr-3">
                        <svg className="h-6 w-6 text-green-600" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5H7a2 2 0 00-2 2v12a2 2 0 002 2h10a2 2 0 002-2V7a2 2 0 00-2-2h-2M9 5a2 2 0 002 2h2a2 2 0 002-2M9 5a2 2 0 012-2h2a2 2 0 012 2" />
                        </svg>
                      </div>
                      <h3 className="text-xl font-bold text-gray-800">Placement Highlights</h3>
                    </div>

                    <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                      {selectedCollege.placements.averagePackage && (
                        <div className="bg-white/80 backdrop-blur-sm p-4 rounded-xl border border-green-100 hover:shadow-md transition-shadow">
                          <div className="text-3xl font-bold text-green-600 mb-1">
                            {selectedCollege.placements.averagePackage}
                          </div>
                          <p className="text-sm text-gray-600">Average Package</p>
                          <div className="mt-2 h-1 w-full bg-green-100 rounded-full overflow-hidden">
                            <div className="h-full bg-green-500 rounded-full" style={{ width: '85%' }}></div>
                          </div>
                        </div>
                      )}

                      {selectedCollege.placements.highestPackage && (
                        <div className="bg-white/80 backdrop-blur-sm p-4 rounded-xl border border-green-100 hover:shadow-md transition-shadow">
                          <div className="text-3xl font-bold text-emerald-600 mb-1">
                            {selectedCollege.placements.highestPackage}
                          </div>
                          <p className="text-sm text-gray-600">Highest Package</p>
                          <div className="mt-2 h-1 w-full bg-emerald-100 rounded-full overflow-hidden">
                            <div className="h-full bg-emerald-500 rounded-full" style={{ width: '95%' }}></div>
                          </div>
                        </div>
                      )}

                      {selectedCollege.placements.placementRate && (
                        <div className="bg-white/80 backdrop-blur-sm p-4 rounded-xl border border-green-100 hover:shadow-md transition-shadow">
                          <div className="text-3xl font-bold text-teal-600 mb-1">
                            {selectedCollege.placements.placementRate}%
                          </div>
                          <p className="text-sm text-gray-600">Placement Rate</p>
                          <div className="mt-2 h-1 w-full bg-teal-100 rounded-full overflow-hidden">
                            <div 
                              className="h-full bg-teal-500 rounded-full" 
                              style={{ width: `${selectedCollege.placements.placementRate}%` }}
                            ></div>
                          </div>
                        </div>
                      )}
                    </div>

                    {selectedCollege.placements.topRecruiters?.length > 0 && (
                      <div className="mt-6">
                        <h4 className="font-medium text-gray-700 mb-3">Top Recruiters</h4>
                        <div className="flex flex-wrap gap-2">
                          {selectedCollege.placements.topRecruiters.map((recruiter, index) => (
                            <span 
                              key={index} 
                              className="px-3 py-1.5 bg-white text-sm font-medium text-gray-700 rounded-full border border-gray-200 shadow-sm hover:shadow transition-shadow"
                            >
                              {recruiter}
                            </span>
                          ))}
                        </div>
                      </div>
                    )}
                  </div>
                )}

                {/* Specializations */}
                {selectedCollege.specializations?.length > 0 && (
                  <div className="bg-gradient-to-br from-purple-50 to-indigo-50 p-6 rounded-2xl border border-purple-100">
                    <div className="flex items-center mb-4">
                      <div className="p-2 bg-purple-100 rounded-lg mr-3">
                        <svg className="h-6 w-6 text-purple-600" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9.663 17h4.673M12 3v1m6.364 1.636l-.707.707M21 12h-1M4 12H3m3.343-5.657l-.707-.707m2.828 9.9a5 5 0 117.072 0l-.548.547A3.374 3.374 0 0014 18.469V19a2 2 0 11-4 0v-.531c0-.895-.356-1.754-.988-2.386l-.548-.547z" />
                        </svg>
                      </div>
                      <h3 className="text-xl font-bold text-gray-800">Available Specializations</h3>
                    </div>
                    <div className="flex flex-wrap gap-2">
                      {selectedCollege.specializations.map((spec, index) => {
                        const colors = [
                          'bg-blue-100 text-blue-800 border-blue-200',
                          'bg-green-100 text-green-800 border-green-200',
                          'bg-purple-100 text-purple-800 border-purple-200',
                          'bg-amber-100 text-amber-800 border-amber-200',
                          'bg-rose-100 text-rose-800 border-rose-200',
                          'bg-indigo-100 text-indigo-800 border-indigo-200',
                        ];
                        const colorClass = colors[index % colors.length];

                        return (
                          <span 
                            key={index} 
                            className={`px-4 py-2 text-sm font-medium rounded-full border ${colorClass} hover:shadow-md transition-all duration-200 transform hover:-translate-y-0.5`}
                          >
                            {spec}
                          </span>
                        );
                      })}
                    </div>
                  </div>
                )}

                {/* Description */}
                {selectedCollege.description && (
                  <div className="bg-white p-6 rounded-2xl border border-gray-200 shadow-sm">
                    <div className="flex items-center mb-4">
                      <div className="p-2 bg-amber-100 rounded-lg mr-3">
                        <svg className="h-6 w-6 text-amber-600" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 16h-1v-4h-1m1-4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
                        </svg>
                      </div>
                      <h3 className="text-xl font-bold text-gray-800">About {selectedCollege.shortName || 'the College'}</h3>
                    </div>
                    <div className="prose prose-indigo max-w-none">
                      <p className="text-gray-700 leading-relaxed">
                        {selectedCollege.description}
                      </p>

                      {/* Additional Info */}
                      <div className="mt-6 grid grid-cols-1 md:grid-cols-2 gap-4">
                        {selectedCollege.campusSize && (
                          <div className="flex items-start">
                            <div className="flex-shrink-0">
                              <svg className="h-5 w-5 text-gray-400" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 21V5a2 2 0 00-2-2H7a2 2 0 00-2 2v16m14 0h2m-2 0h-5m-9 0H3m2 0h5M9 7h1m-1 4h1m4-4h1m-1 4h1m-5 10v-5a1 1 0 011-1h2a1 1 0 011 1v5m-4 0h4" />
                              </svg>
                            </div>
                            <div className="ml-3">
                              <p className="text-sm font-medium text-gray-500">Campus Size</p>
                              <p className="text-sm text-gray-900">{selectedCollege.campusSize}</p>
                            </div>
                          </div>
                        )}

                        {selectedCollege.faculties && (
                          <div className="flex items-start">
                            <div className="flex-shrink-0">
                              <svg className="h-5 w-5 text-gray-400" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 4.354a4 4 0 110 5.292M15 21H3v-1a6 6 0 0112 0v1zm0 0h6v-1a6 6 0 00-9-5.197M13 7a4 4 0 11-8 0 4 4 0 018 0z" />
                              </svg>
                            </div>
                            <div className="ml-3">
                              <p className="text-sm font-medium text-gray-500">Faculty</p>
                              <p className="text-sm text-gray-900">{selectedCollege.faculties} Experienced Faculty</p>
                            </div>
                          </div>
                        )}

                        {selectedCollege.students && (
                          <div className="flex items-start">
                            <div className="flex-shrink-0">
                              <svg className="h-5 w-5 text-gray-400" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17 20h5v-2a3 3 0 00-5.356-1.857M17 20H7m10 0v-2c0-.656-.126-1.283-.356-1.857M7 20H2v-2a3 3 0 015.356-1.857M7 20v-2c0-.656.126-1.283.356-1.857m0 0a5.002 5.002 0 019.288 0M15 7a3 3 0 11-6 0 3 3 0 016 0zm6 3a2 2 0 11-4 0 2 2 0 014 0zM7 10a2 2 0 11-4 0 2 2 0 014 0z" />
                              </svg>
                            </div>
                            <div className="ml-3">
                              <p className="text-sm font-medium text-gray-500">Students</p>
                              <p className="text-sm text-gray-900">{selectedCollege.students}+ Students</p>
                            </div>
                          </div>
                        )}

                        {selectedCollege.accreditation && (
                          <div className="flex items-start">
                            <div className="flex-shrink-0">
                              <svg className="h-5 w-5 text-gray-400" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12l2 2 4-4m5.618-4.016A11.955 11.955 0 0112 2.944a11.955 11.955 0 01-8.618 3.04A12.02 12.02 0 003 9c0 5.591 3.824 10.29 9 11.622 5.176-1.332 9-6.03 9-11.622 0-1.042-.133-2.052-.382-3.016z" />
                              </svg>
                            </div>
                            <div className="ml-3">
                              <p className="text-sm font-medium text-gray-500">Accreditation</p>
                              <p className="text-sm text-gray-900">{selectedCollege.accreditation}</p>
                            </div>
                          </div>
                        )}
                      </div>
                    </div>
                  </div>
                )}
              </div>

              <div className="p-6 bg-gradient-to-r from-gray-50 to-gray-100 border-t flex flex-col sm:flex-row justify-between items-center space-y-3 sm:space-y-0">
                <div className="flex items-center">
                  <div className="flex-shrink-0 h-10 w-10 rounded-full bg-blue-100 flex items-center justify-center">
                    <svg className="h-6 w-6 text-blue-600" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 8l7.89 5.26a2 2 0 002.22 0L21 8M5 19h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v10a2 2 0 002 2z" />
                    </svg>
                  </div>
                  <div className="ml-3">
                    <p className="text-sm font-medium text-gray-700">Need more information?</p>
                    <a href="#" className="text-sm text-blue-600 hover:underline">Contact the college directly</a>
                  </div>
                </div>

                <div className="flex flex-col sm:flex-row space-y-2 sm:space-y-0 sm:space-x-3 w-full sm:w-auto">
                  <button
                    onClick={closeModal}
                    className="px-5 py-2.5 border border-gray-300 bg-white text-gray-700 rounded-lg hover:bg-gray-50 transition-colors font-medium"
                  >
                    Close
                  </button>
                  <button
                    onClick={() => handleGetDirections(selectedCollege)}
                    className="px-5 py-2.5 bg-gradient-to-r from-blue-600 to-blue-700 text-white rounded-lg hover:from-blue-700 hover:to-blue-800 transition-all font-medium flex items-center justify-center"
                  >
                    <MapIcon className="h-5 w-5 mr-2" />
                    Get Directions
                  </button>
                </div>
              </div>
            </div>
          </div>
        )}

        {/* No Results */}
        {filteredColleges.length === 0 && (
          <div className="text-center py-12">
            <AcademicCapIcon className="mx-auto h-12 w-12 text-gray-400" />
            <h3 className="mt-2 text-sm font-medium text-gray-900">No colleges found</h3>
            <p className="mt-1 text-sm text-gray-500">Try adjusting your search criteria.</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Colleges;
